import React, { useState } from 'react';
import styled from 'styled-components';
import * as mixins from 'styles/mixins';
import LoginForm from 'path-to-your-login-form-component'; // Import your login form component

const Wrapper = styled.div`
  ${mixins.fullScreen};
  ${mixins.bgWhite};
  padding: 33px;
`;

const Logo = styled.img`
  display: block;
  width: 122px;
  height: 27px;
`;

const LandingPage = () => {
  const [showLoginForm, setShowLoginForm] = useState(false);

  // Function to handle directly showing the login form for group call
  const handleGroupCallClick = () => {
    // Perform any necessary logic or checks here if needed before showing the login form
    setShowLoginForm(true);
  };

  return (
    <Wrapper>
      <Logo
        src="/icons/ic-logo-horizontal.svg"
        alt="Sendbird horizontal logo svg icon"
      />
      {showLoginForm ? (
        <LoginForm /> // Render the login form directly
      ) : (
        <button onClick={handleGroupCallClick}>
          Start Group Call
        </button>
      )}
    </Wrapper>
  );
};

export default LandingPage;
